import React from "react";
import ReactDOM from "react-dom";
// import App from "./components/App";

function Beyonce (props) {
    return ( <div>
        <h2>{props.name}</h2>
		<img src = {props.src} alt = {props.alt}/>
		<p>{props.tel}</p>
		<p>{props.mail}</p>
    </div>);
}

