        <Beyonce name = "Beyonce" src = "https://blackhistorywall.files.wordpress.com/2010/02/picture-device-independent-bitmap-119.jpg" alt = "Beyonce's Image" tel = "+123 456 789" mail = "b@beyonce.com"/>
