import React from "react";
import ReactDOM from "react-dom";
// import App from "./components/App";

function Beyonce (props) {
    return <div>
        <h2>{props.name}</h2>
		<img src = "https://blackhistorlywal.files.wordpress.com/2010/02/picture-device-independent-bitmap-119.jpg" alt = "Beyonce's Image" />
		<p>+123 456 789</p>
		<p>b@beyonce.com</p>
    </div>;
}

function Jack_Bauer () {
    return <div>
    <h2>Jack Bauer</h2>
    <img src = "https://pbs.twimg.com/profile_images/625247595825246208/X3XLea04_400x400.jpg" alt = "Jack Bauer's Image" />
    <p>+987 654 321</p>
    <p>jack@nowhere.com</p>
    </div>;
}

function Chuck_Norris () {
    return <div>
        <h2>Chuck Norris</h2>
		<img src = "https://i.pinimg.com/originals/e3/94/47/e39447de921955826b1e498ccf9a39af.png" alt = "Chuck Norris's Image" />
		<p>+918 372 574</p>
		<p>gmail@chucknorris.com</p>
    </div>;
}

ReactDOM.render(<div>
        <Beyonce/>
        <Jack_Bauer/>
        <Chuck_Norris/>
</div>, document.getElementById("root"));

